import React, { Component } from 'react';
import './App.css';
import {default as GamesDropdown} from './GamesDropdown';

class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      games: [],
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleOnClick = this.handleOnClick.bind(this);
  }

  handleOnClick(e) {
    this.setState({
      games: [this.state.games, this.props.gameName]
    });
  }

  handleChange(e) {
    this.props.handleUpdateGameName(e.target.value)
  }

  render() {
    return (
      <div id="add-game-container">
        <input value={this.props.gameName} id="tf-game-to-add" className="text-field fa" placeholder="&#xf11b; Game to Add" onChange={this.handleChange}></input>
        <button id="btn-game-to-add" className="btn fa fa-plus" onClick={this.handleOnClick}></button>

        <div>
          <GamesDropdown games={this.state.games} />
          <button id="btn-game-to-remove" className="btn fa fa-trash"></button>
          <button id="btn-game-to-top" className="btn fa fa-arrow-up"></button>
        </div>
      </div>
    );
  }
}

export default Header;
