export default function setSelectedGame(game) {
  return {
    type: "SELECT_GAME",
    game: game
  }
}

export function removeGame(game) {
  const games = store.getState().games;

  for (let i = 0; i < games.length; i++)
    if (games[i] === game) {
      games.splice(i, 1);
      break;
    }

  return {
    type: "SET_GAMES",
    games: games
  }
}

export function addGame(gameName) {
  const games = store.getState().games;

  games.concat(gameName);

  return {
    type: "SET_GAMES",
    games: games
  }
}
