import React, { Component } from 'react';
import './App.css';
import ReactCSSTransitionReplace from 'react-css-transition-replace';

class HeaderStatus extends Component {
  constructor(props) {
    super(props);

    this.state = {
      statusBad: this.props.statusBad,
      statusGood: this.props.statusGood
    }
  }

  // componentDidUpdate(prevProps, prevState) {
  //   const { statusBad, statusGood } = this.state;
  //   console.log("prev: " + prevProps.statusBad + " this.stats: " + statusBad + " this.props: " + this.props.statusBad + " prevState: " + prevState.statusBad);
  //   if (statusBad) {
  //     this.setState({statusBad: true});
  //   //  this.props.handleStatus(true, false);
  //     //setTimeout(() => {this.props.handleStatus(false, false)}, 3000);
