  //     this.setState({statusGood: this.props.statusGood});
  //     setTimeout(() => {this.setState({statusGood: false})}, 3000);
  //   }
  // }

  // componentWillReceiveProps(nextProps) {
  //   console.log("will recieve: " + this.state.statusBad);
  //   if (nextProps.statusBad !== false)
  //     this.setState({statusBad: nextProps.statusBad});
  // }

  render() {
    const { statusBad, statusGood } = this.state;
    console.log(statusBad + " " + this.props.statusBad);
    if (!this.props.statusGood && !this.props.statusBad)
      return (
        <ReactCSSTransitionReplace
          transitionName="cross-fade"
          transitionEnterTimeout={500}
          transitionLeaveTimeout={500}
        >
          <div key="transitioning-component-0"></div>
        </ReactCSSTransitionReplace>
      );
    else if (statusGood)
      return (
        <ReactCSSTransitionReplace
          transitionName="cross-fade"
          transitionEnterTimeout={500}
          transitionLeaveTimeout={500}
        >
          <div key="transitioning-component-1" className="status-good">Successful</div>
        </ReactCSSTransitionReplace>
      );
    else if (this.props.statusBad) {
      console.log("in dere");
  //    setTimeout(() => {this.setState({statusBad: false})}, 3000);
      //setTimeout(() => {this.props.handleStatus(false, false)}, 3000);

      return (
        <ReactCSSTransitionReplace
          transitionName="cross-fade"
          transitionEnterTimeout={500}
          transitionLeaveTimeout={500}
        >
          <div key="transitioning-component-2" className="status-bad">Duplicate, nothing added</div>
        </ReactCSSTransitionReplace>
      );
    }
  }
}

export default HeaderStatus;
