  }

  render() {
    if (this.props.games.length == 0) {
      return (
        <select id="cb-game-to-add" className="combo-box">
          <option id="cb-initial-option">No Games to Show</option>
        </select>
      );
    } else {
      return (
        <select id="cb-game-to-add" className="combo-box">
          <option> { this.games } </option>
        </select>
      );
    }
  }
}

export default GamesDropdown;
