export default function setSelectedGame(game) {
  return {
    type: "SELECT_GAME",
    game: game
  }
}

export function setGames(games) {
  for (let i = 0; i < games.length; i++)
    if (games[i] === this.props.selectedGame) {
      games.splice(i, 1);
      break;
    }

  return {
    type: "SET_GAMES",
    games: games
  }
}
