//Action that sets the selected game.
export const setSelectedGame = (game) => {
  return {
    type: "SELECT_GAME",
    game: game
  }
}

//Action that sets the games array.
export const setGames = (games) => {
  return {
    type: "SET_GAMES",
    games: games
  }
}
