import React, { Component } from 'react';
import {default as Header} from './Header';
import {default as Streams} from './Streams';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      gameName: "",
      selectedGame: ""
    };

    this.handleGameNameChange = this.handleGameNameChange.bind(this);
    this.handleGameSelectChange = this.handleGameSelectChange.bind(this);
  }

  handleGameNameChange(name) {
    this.setState({gameName: name});
  }

  handleGameSelectChange(name) {
    console.log(name);
    this.setState({selectedGame: name});
  }

  render() {
    return (
      <div>
        <Header
          handleGameNameChange={this.handleGameNameChange}
          handleGameSelectChange ={this.handleGameSelectChange}
        />
        <Streams gameName={this.state.selectedGame} />
      </div>
    );
  }
}

export default App;
