import React, { Component } from 'react';
import {default as Header} from './Header';
import {default as Streams} from './Streams';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      gameName: "",
      selectedGame: ""
    };

    this.handleUpdateGameName = this.handleUpdateGameName.bind(this);
  }

  handleUpdateGameName(e) {
    console.log(e);
    this.setState({gameName: e.target.value});
  }

  render() {
    return (
      <div>
        <Header handleUpdateGameName={this.state.handleUpdateGameName} />
        <Streams gameName={this.state.gameName} />
      </div>
    );
  }
}

export default App;
