import React, { Component } from 'react';
import './App.css';

class GamesDropdown extends Component {
  constructor(props) {
    super(props);

    this.state = {
      gameValue: this.props.gameValue
    };

    this.handleOnChange_GamesDropdown = this.handleOnChange_GamesDropdown.bind(this);
  }

  componentDidUpdate(prevProps) {
    if (this.props.gameValue !== prevProps.gameValue)
      this.setState({gameValue: this.props.gameValue});
  }

  handleOnChange_GamesDropdown(e) {
    let gameValue = e.target.value;

    this.setState({gameValue: gameValue});
    this.props.handleGameSelectChange(gameValue);
  }

  render() {
    if (this.props.games.length === 0) {
      return (
        <select id="cb-game-to-add" className="combo-box">
          <option id="cb-initial-option">No Games to Show</option>
        </select>
      );
    } else {
      let games = [];
      for (let i = 0; i < this.props.games.length; i++) {
        games.push(<option value={this.props.games[i]}>{ this.props.games[i] }</option>);
      }

      return (
        <select value={this.state.gameValue} onChange={this.handleOnChange_GamesDropdown} id="cb-game-to-add" className="combo-box">
          { games }
        </select>
      );
    }
  }

  renderGameOptions(game) {
    return <option>{game}</option>;
  }
}

export default GamesDropdown;
