import React, { Component } from 'react';
import './App.css';

class Post extends Component {
  render() {
    return (
      <div>fuck</div>
    );
  }
}

class Streams extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      posts: []
    };
  }

  renderPost(data, i) {
    return (
      <div>
        { data[i].channel.status }
      </div>
    );
  }

  openRequest(request, gameName) {
    let gameNameSplit = gameName.split(" ");
    let gameUrl = gameNameSplit.join("%20");

    request.open("GET", "https://api.twitch.tv/kraken/streams/?game=" + gameUrl, true);

    return request;
  }

  createDivForNoStream() {
    return (
      <div>
        <h1>Invalid game name or no one is streaming this game</h1>
      </div>
    );
  }

  componentDidMount() {
    let gameName = "Super Smash Bros. Melee";
    let request = new XMLHttpRequest();
    request = this.openRequest(request, gameName);
    request.setRequestHeader('Client-ID', "mr14v2oijk1jv4n8u49ixkv6raofcd");
    request.send();

    request.onload = function () {
      let data = JSON.parse(request.response);

      if (request.status >= 200 && request.status < 400) {
        this.setState({
          isLoaded: true,
          posts: data.streams
        });
      }
    }.bind(this);
  }

  render() {
    const { isLoaded, posts } = this.state;

    if (!isLoaded) {
      return <div>Loading...</div>
    } else if (posts.length == 0) {
      return  this.createDivForNoStream();
    } else {
      let contents = [];
      for (var i = 0; i < posts.length; i++) {
        contents.push(this.renderPost(posts, i));
      }
      console.log(contents);

      return (
        <div>
          { contents }
        </div>
      );
    }
  }
}

export default PostSection;
