import React, { Component } from 'react';
import './App.css';

class GamesDropdown extends Component {
  constructor(props) {
    super(props);

    this.renderGameOptions = this.renderGameOptions.bind(this);
  }

  renderGameOptions(game) {
    return <option>{game}</option>;
  }

  render() {
    if (this.props.games.length == 0) {
      return (
        <select id="cb-game-to-add" className="combo-box">
          <option id="cb-initial-option">No Games to Show</option>
        </select>
      );
    } else {
      return (
        <select id="cb-game-to-add" className="combo-box">
          this.props.games.map(renderGameOptions);
        </select>
      );
    }
  }
}

export default GamesDropdown;
