import React, { Component } from 'react';
import './App.css';
import {default as GamesDropdown} from './GamesDropdown';

class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      games: []
    }
  }

  handleOnClick(e) {
    const { games } = this.state;

    games.push(this.props.gameName);
  }

  render() {
    return (
      <div id="add-game-container">
        <input id="tf-game-to-add" className="text-field fa" placeholder="&#xf11b; Game to Add" onChange={this.props.handleUpdateGameName}></input>
        <button id="btn-game-to-add" className="btn fa fa-plus" onClick={this.handleOnClick}></button>

        <div>
          <GamesDropdown games={this.state.games} />
          <button id="btn-game-to-remove" className="btn fa fa-trash"></button>
          <button id="btn-game-to-top" className="btn fa fa-arrow-up"></button>
        </div>
      </div>
    );
  }
}

export default Header;
