import React, { Component } from 'react';
import './App.css';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

class HeaderStatus extends Component {
  constructor(props) {
    super(props);

    this.state = {
      statusBad: this.props.statusBad,
      statusGood: this.props.statusGood
    }
  }

  componentDidUpdate(prevProps) {
    if (this.props.statusBad !== prevProps.statusBad) {
      this.setState({statusBad: this.props.statusBad});
    //  setTimeout(() => {this.setState({statusBad: false})}, 3000);
    }

    if (this.props.statusGood !== prevProps.statusGood) {
      this.setState({statusGood: this.props.statusGood});
      //setTimeout(() => {this.setState({statusGood: false})}, 3000);
    }
  }

  render() {
    const { statusBad, statusGood } = this.state;

    if (!statusGood && !statusBad)
      return <div></div>;
    else if (statusGood)
      return (
        <ReactCSSTransitionGroup
          transitionName="cross-fade"
          transitionAppear={true}
          transitionAppearTimeout={500}
          transitionEnterTimeout={false}
          transitionLeaveTimeout={500}
        >
          <div key="transitioning-component-1" className="status-good">Successful</div>
        </ReactCSSTransitionGroup>
      );
    else if (statusBad)
      return (
        <ReactCSSTransitionGroup
          transitionName="cross-fade"
          transitionEnterTimeout={1000}
          transitionLeaveTimeout={1000}
        >
          <div key="transitioning-component-2" className="status-bad">Duplicate, nothing added</div>
        </ReactCSSTransitionGroup>
      );
  }
}

export default HeaderStatus;
