import React, { Component } from 'react';
import './App.css';

class GamesDropdown extends Component {
  constructor(props) {
    super(props);

    this.state = {
      gameValue: this.props.gameValue
    };
    this.handleGameDropdownChange = this.handleGameDropdownChange.bind(this);
  }

  componentDidMount(prevProps) {
    if (this.props.value !== prevProps.value) {
      this.setState({value: this.props.value});
    }
  }

  handleGameDropdownChange(e) {
    this.props.handleGameSelectChange(e.target.value);
  }

  renderGameOptions(game) {
    return <option>{game}</option>;
  }

  render() {
    if (this.props.games.length == 0) {
      return (
        <select id="cb-game-to-add" className="combo-box">
          <option id="cb-initial-option">No Games to Show</option>
        </select>
      );
    } else {
      let games = [];
      for (let i = 0; i < this.props.games.length; i++) {
        games.push(<option value={this.props.games[i]}>{ this.props.games[i] }</option>);
      }

      return (
        <select value={this.props.value} onChange={this.props.handleGameDropdownChange} id="cb-game-to-add" className="combo-box">
          { games }
        </select>
      );
    }
  }
}

export default GamesDropdown;
