import React, { Component } from 'react';
import './App.css';
import 'font-awesome/css/font-awesome.min.css';
import {default as GamesDropdown} from './GamesDropdown';

/*gameValue and gameName are separate state variables so that GamesDropdown
  isn't updated everytime something is typed in the textbox. Instead it is
  only updated when the add game button is clicked.*/
class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      games: [],
      gameName: "",
      gameValue: ""
    }

    this.handleTextboxGameChange = this.handleTextboxGameChange.bind(this);
    this.handleAddGameClick = this.handleAddGameClick.bind(this);
    this.handleRemoveGameClick = this.handleRemoveGameClick.bind(this);
    this.handleMoveGameToTopButton = this.handleMoveGameToTopButton.bind(this);
    this.setGameComboBoxLocalStorage = this.setGameComboBoxLocalStorage.bind(this);
  }

  setGameComboBoxLocalStorage() {
    const games = this.state.games;

    let toStoreString = games.join(",;:!");
    localStorage.setItem('games', JSON.stringify(toStoreString));
  }

  componentDidMount() {
    let gamesStorage = JSON.parse(localStorage.getItem("games"));
    if (gamesStorage != null) {
      let games = gamesStorage.split(",;:!");
      console.log(games);
      this.setState({games: games});
    }
  }

  handleAddGameClick(e) {
    this.setState({
      games: this.state.games.concat(this.state.gameName),
      gameValue: this.state.gameName
    });

    //When a game is added it becomes the selected game.
    this.props.handleGameSelectChange(this.state.gameName);

    //Clear text box after addition of game.
    this.setState({gameName: ""});

    this.setGameComboBoxLocalStorage();
  }

  handleRemoveGameClick(e) {
    const { games, gameName } = this.state;

    for (let i = 0; i < games.length; i++) {
      if (games[i] === this.props.selectedGame) {
        games.splice(i, 1);
        break;
      }
    }

    this.setState({games: games});

    if (this.state.games.length > 0)
      this.props.handleGameSelectChange(this.state.games[0]);
  }

  handleMoveGameToTopButton(e) {
    const { games, gameName } = this.state;

    for (let i = 0; i < games.length; i++)
      if (games[i] === this.props.selectedGame) {
        games.unshift(games[i]);
        games.splice(i + 1, 1);
        break;
      }

    this.setState({games: games});
  }

  handleTextboxGameChange(e) {
    this.setState({gameName: e.target.value});
  }

  render() {
    return (
      <div id="add-game-container">
        <input value={this.state.gameName} onChange={this.handleTextboxGameChange} id="tf-game-to-add" className="text-field fa" placeholder="&#xf11b; Game to Add"></input>
        <button onClick={this.handleAddGameClick} id="btn-game-to-add" className="btn fa fa-plus"></button>

        <div>
          <GamesDropdown gameValue={this.state.gameValue} games={this.state.games} handleGameSelectChange={this.props.handleGameSelectChange}/>
          <button onClick={this.handleRemoveGameClick} id="btn-game-to-remove" className="btn fa fa-trash"></button>
          <button onClick={this.handleMoveGameToTopButton} id="btn-game-to-top" className="btn fa fa-arrow-up"></button>
        </div>
      </div>
    );
  }
}

export default Header;
